import { mergeProps, splitProps } from "solid-js";
import classNames from "./classnames";
import { useBootstrapPrefix } from "./ThemeProvider";
import BreadcrumbItem from "./BreadcrumbItem";
import { Dynamic } from "solid-js/web";
const defaultProps = {
    as: "nav",
    label: "breadcrumb",
    listProps: {},
};
const Breadcrumb = (p) => {
    const [local, props] = splitProps(mergeProps(defaultProps, p), [
        "bsPrefix",
        "class",
        "listProps",
        "children",
        "label",
        "as",
    ]);
    const prefix = useBootstrapPrefix(local.bsPrefix, "breadcrumb");
    return (<Dynamic component={local.as} aria-label={local.label} class={classNames(local.class)} {...props}>
      <ol {...local.listProps} class={classNames(prefix, local.listProps?.class)}>
        {local.children}
      </ol>
    </Dynamic>);
};
export default Object.assign(Breadcrumb, {
    Item: BreadcrumbItem,
});
