import { mergeProps, splitProps } from "solid-js";
import classNames from "./classnames";
import { useBootstrapPrefix } from "./ThemeProvider";
import PageItem, { Ellipsis, First, Last, Next, Prev } from "./PageItem";
const defaultProps = {};
/**
 * @property {PageItem} Item
 * @property {PageItem} First
 * @property {PageItem} Prev
 * @property {PageItem} Ellipsis
 * @property {PageItem} Next
 * @property {PageItem} Last
 */
const Pagination = (p) => {
    const [local, props] = splitProps(mergeProps(defaultProps, p), ["bsPrefix", "class", "size"]);
    const decoratedBsPrefix = useBootstrapPrefix(local.bsPrefix, "pagination");
    return (<ul {...props} class={classNames(local.class, decoratedBsPrefix, local.size && `${decoratedBsPrefix}-${local.size}`)}/>);
};
export default Object.assign(Pagination, {
    First,
    Prev,
    Ellipsis,
    Item: PageItem,
    Next,
    Last,
});
